import React, {useState} from "react";
import 'bootstrap/dist/css/bootstrap.css';
import {useEffect} from "react";
import crowdfundInstance from '../contract/crowdfundInstance';
import crowdfundProject from '../contract/crowdfundProjectInstance';
import web3 from '../contract/web3';
import { BrowserRouter, Route, Switch, Link } from "react-router-dom";
import {withRouter} from "react-router-dom";

function PayoutRequest({pr}) {
    const [payoutRequest, setPayoutRequest] = useState([]);

    useEffect(() => {
        setPayoutRequest(pr)
    }, [pr]);

    return (
        <li>
            <div className="bg-light m-5 p-3 fpWrapper">
                <h3 className="p-3">{pr.ethAmount}</h3>
                <div className="p-3">{pr.intention}</div>
                <div className="p-3">{pr.reporting}</div>
                <div className="p-3">{pr.achiveBy}</div>
                {/*<div className="p-3">{pr.countOfApprovers}</div>*/}
            </div>
        </li>
    )
}
