import React, {useState} from "react";
import 'bootstrap/dist/css/bootstrap.css';
import {useEffect} from "@types/react";

export default function FundraisingProject({fp, isModeration}) {
    const [mounted, setMounted] = useState(false);
    const [fProj, setFp] = React.useState(fp);

    if(!mounted) {
        setFp(fp);
    }

    console.log(fProj);
    function validate() {
        fetch(`http://localhost:18080/moderation/${fProj.id}?` + new URLSearchParams({
            isValid: true,
        }), {
            method: 'POST',
            headers: new Headers({
                "Authorization": sessionStorage.jwtToken
            })
        }).then(response => response.json())
            .then(data => {
                console.log(data);
                setFp(data);
            });
    }

    useEffect(() =>{
        setMounted(true)
    },[])

    return (
        <li>
            <div className="bg-light m-5 p-3 fpWrapper">
                <h2 className="p-3">{fProj.title}</h2>
                <div className="p-3">{fProj.description}</div>
                <div className="row p-3">
                    <div className="col-sm">User: {fProj.founder.username}</div>
                    <div className="col-sm">Goal: </div>
                    <div className="col-sm">Due date: </div>
                    <div className="col-sm">Raised: </div>
                </div>
                <div className="p-3">Tags: </div>
                {isModeration > 0 &&
                    <div>
                        <div className="p-3">Status: {fProj.status}</div>
                        <div className="p-1">
                            <button type="button" className="btn btn-primary btn-block m-2" onClick={validate()}>Approve</button>
                            <button type="button" className="btn btn-primary btn-block m-2">Reject</button>
                        </div>
                    </div>
                }
            </div>
        </li>
    )
}
