import React, {useState} from "react";
import 'bootstrap/dist/css/bootstrap.css';
import {useEffect} from "react";


export default function FundraisingProject({fp, isModeration, isSelfProfile}) {
    const [fProj, setFp] = React.useState(fp);

    function approve() {
        fetch(`http://localhost:18080/moderation/${fProj.fundraisingProjectId}?` + new URLSearchParams({
            isValid: true,
        }), {
            method: 'POST',
            headers: new Headers({
                "Authorization": sessionStorage.jwtToken
            })
        }).then(response => response.json())
            .then(data => {
                setFp(data);
            });
    }

    function reject() {
        fetch(`http://localhost:18080/moderation/${fProj.fundraisingProjectId}?` + new URLSearchParams({
            isValid: false,
        }), {
            method: 'POST',
            headers: new Headers({
                "Authorization": sessionStorage.jwtToken
            })
        }).then(response => response.json())
            .then(data => {
                setFp(data);
            });
    }

    function startFp() {
        fetch(`http://localhost:18080/fundraising-projects/start`, {
            method: 'POST',
            headers: new Headers({
                "Authorization": sessionStorage.jwtToken
            })
        }).then(response => response.json())
            .then(data => {
                setFp(data);
            });
    }

    useEffect(() =>{
        setFp(fp)
    },[fp])

    console.log(fProj);

    return (
        <li>
            <div className="bg-light m-5 p-3 fpWrapper">
                <h2 className="p-3">{fProj.title}</h2>
                <div className="p-3">{fProj.description}</div>
                <div className="row p-3">
                    <div className="col-sm">User: {fProj.founder.username}</div>
                    <div className="col-sm">Goal: </div>
                    <div className="col-sm">Due date: </div>
                    <div className="col-sm">Raised: </div>
                </div>
                <div className="p-3">Tags: </div>
                {isModeration > 0 &&
                    <div>
                        <div className="p-3">Status: {fProj.status}</div>
                        <div className="p-1">
                            <button type="button" className="btn btn-primary btn-block m-2" onClick={() => approve()}>Approve</button>
                            <button type="button" className="btn btn-primary btn-block m-2" onClick={() => reject()}>Reject</button>
                        </div>
                    </div>
                }
                {isSelfProfile > 0 && !(isModeration > 0) &&
                    <div>
                        <div className="p-3">Status: {fProj.status}</div>
                    </div>
                }
                {fp.status == "APPROVED" &&
                    <button type="button" className="btn btn-primary btn-block m-2" onClick={() => startFp()}>Start company</button>}
            </div>
        </li>
    )
}
