import React, {useState} from "react";
import 'bootstrap/dist/css/bootstrap.css';
import {useEffect} from "react";

export default function PayoutRequest({pr}) {
    const [payoutRequest, setPayoutRequest] = useState([]);

    useEffect(() => {
        setPayoutRequest(pr)
    }, [pr]);

    return (
        <li>
            <div className="bg-light m-5 p-3 fpWrapper" style={{borderRadius: "15px"}}>
                <h3 className="p-3">{payoutRequest.ethAmount}</h3>
                <div className="p-3">{payoutRequest.intention}</div>
                <div className="p-3">{payoutRequest.reporting}</div>
                <div className="p-3">{payoutRequest.achiveBy.substring(0, payoutRequest.indexOf(' '))}</div>
                {/*<div className="p-3">{pr.countOfApprovers}</div>*/}
            </div>
        </li>
    )
}
