import React, {useState} from "react";
import 'bootstrap/dist/css/bootstrap.css';
import {useEffect} from "react";
import web3 from "./contract/web3";

export default function PayoutRequest({pr, fpId}) {
    const [isLoaded, loaded] = React.useState(false);
    const [isInvestor, setIsInvestor] = React.useState(false);
    const [isApproved, setIsApproved] = React.useState(false);

    useEffect(() =>{
        loadData();
    }, )

    async function loadData() {
        // isCurrentUser investor
        await fetch("http://localhost:18080/user/isInvestor?fpId=" + fpId, {
            method: "GET",
            headers: new Headers({
                "Authorization": sessionStorage.jwtToken
            })
        }).then(response => response.json())
            .then(data => setIsInvestor(data));
        if (isInvestor) {
            await fetch("")
        }
        loaded(true);
    }

    if (isLoaded === false) {
        return <div></div>
    }

    return (
        <li>
            <div className="bg-light m-5 p-3 fpWrapper" style={{borderRadius: "15px"}}>
                <h3 className="p-3">Requested sum: {pr.ethAmount}</h3>
                <div className="p-3">Intention: {pr.intention}</div>
                <div className="p-3">Promised report: {pr.reporting}</div>
                <div className="p-3">Due date: {pr.achieveBy.substring(0, pr.achieveBy.indexOf('T'))}</div>
                {/*<div className="p-3">{pr.countOfApprovers}</div>*/}
            </div>
        </li>
    )
}
