import React, {useState} from "react";
import 'bootstrap/dist/css/bootstrap.css';
import {Link, withRouter} from "react-router-dom";

function UserCard({user}) {
    return (
        <li>
            <div className="bg-light m-5 p-3 fpWrapper">
                <Link to={`/users-statistics/${user.id}`}><h2 className="p-3">{user.username}</h2></Link>
                <Link to={`/users-statistics/${user.id}`}><button className="btn btn-primary">Show investments</button></Link>
            </div>
        </li>
    )
}

export default withRouter(UserCard);
