import React from "react";
import 'bootstrap/dist/css/bootstrap.css';

export default function FundraisingProject({fp, isModeration}) {
    function validate(fpId) {
        fetch(`http://localhost:18080/moderation/${fpId}` + new URLSearchParams({
            isValid: true,
        }), {
            method: 'GET',
            headers: new Headers({
                "Authorization": sessionStorage.jwtToken
            })
        }).then(response => response.json())
            .then(data => {
                console.log(data);
                setFps(data);
            });
    }

    return (
        <li>
            <div className="bg-light m-5 p-3 fpWrapper">
                <h2 className="p-3">{fp.title}</h2>
                <div className="p-3">{fp.description}</div>
                <div className="row p-3">
                    <div className="col-sm">User: {fp.founder.username}</div>
                    <div className="col-sm">Goal: </div>
                    <div className="col-sm">Due date: </div>
                    <div className="col-sm">Raised: </div>
                </div>
                <div className="p-3">Tags: </div>
                {isModeration > 0 &&
                    <div>
                        <div className="p-3">Status: {fp.status}</div>
                        <div className="p-1">
                            <button type="button" className="btn btn-primary btn-block m-2" onClick={validate(fp.id)}>Approve</button>
                            <button type="button" className="btn btn-primary btn-block m-2">Reject</button>
                        </div>
                    </div>
                }
            </div>
        </li>
    )
}
