import React, {Component, useState} from "react";
import {add} from "react-modal/lib/helpers/classList";

export default function CreateFP() {

    const [files, setFiles] = useState([]);

    async function requestFP(title, description, amountGoal, days) {
        fetch(`http://localhost:18080/fundraising-projects/create`, {
            method: 'POST',
            headers: new Headers({
                'Content-Type': 'application/json',
                "Authorization": sessionStorage.jwtToken
            }),
            body: JSON.stringify(
                {
                    "title": title,
                    "description": description,
                    "amountGoal": amountGoal,
                    "days": days
                    }
            )
        }).then(response => response.json())
            .then(fp => {
                fetch("http://localhost:18080/api/fundraising-projects/upload-files" + fp.fundraisingProjectId)
            });
    }

    function addFile(event) {
        setFiles([...files,event.target.files[0]]);
        document.getElementById("selectedFilesWrapper").style.display = "block";
        document.getElementById("selectedFilesWrapper").innerHTML +=
            `<div>${event.target.files[0].name}</div>`
        event.target.value = '';
    }

    return (
        <div className="create-fp-wrapper">
            <div className="create-fp-inner" style={{width: "800px", marginTop: "230px"}}>
                <form className="createFpForm">
                    <h3>Start Fundraising Company</h3>

                    <div className="form-group">
                        <label>Title</label>
                        <input id="title" type="text" className="form-control" placeholder="Title" />
                    </div>

                    <div className="form-group">
                        <label>Brief description</label>
                        <textarea id="description" className="form-control" style={{height: "120px"}}/>
                    </div>

                    <div className="form-group">
                        <label>Goal</label>
                        <input id="amountGoal" type="text" className="form-control" placeholder="Goal" />
                    </div>

                    <div className="form-group">
                        <label>Days</label>
                        <input id="days" type="text" className="form-control" placeholder="Days" />
                    </div>

                    <div className="form-group">
                        <label>Tell advanced story of fundraising company</label>
                        <textarea id="description" className="form-control" style={{height: "380px"}}/>
                    </div>

                    <div id="fileUploadWrapper" className="form-group">
                        <input type="file" onChange={addFile}/>
                    </div>

                    <div id="selectedFilesWrapper" style={{display: 'none'}}>
                        <label>Selected files: </label>
                    </div>

                    <br/>
                    <button type="button" className="btn btn-primary btn-block"
                            onClick={(event) => requestFP(
                                document.getElementById('title').value,
                                document.getElementById('description').value,
                                document.getElementById('amountGoal').value,
                                document.getElementById('days').value,
                            )}>Create Fundraising Project</button>
                </form>
            </div>
        </div>
    );
}
