import React, {useState} from "react";
import 'bootstrap/dist/css/bootstrap.css';
import {useEffect} from "react";
import crowdfundInstance from '../contract/crowdfundInstance';
import crowdfundProject from '../contract/crowdfundProjectInstance';
import web3 from '../contract/web3';

export default function FundraisingProject({fp, isModeration, isSelfProfile}) {
    const [fProj, setFp] = React.useState(fp);
    const [isInApprovedState, setIsInApprovedState] = React.useState(false);
    const [isModerationRequired, setIsModerationRequired] = React.useState(false);
    const [accounts, setAccounts] = React.useState([]);

    function approve() {
        fetch(`http://localhost:18080/moderation/${fProj.fundraisingProjectId}?` + new URLSearchParams({
            isValid: true,
        }), {
            method: 'POST',
            headers: new Headers({
                "Authorization": sessionStorage.jwtToken
            })
        }).then(response => response.json())
            .then(data => {
                setFp(data);
                setIsModerationRequired(fProj.status == "NOT_CONSIDERED");
            });
    }

    function reject() {
        fetch(`http://localhost:18080/moderation/${fProj.fundraisingProjectId}?` + new URLSearchParams({
            isValid: false,
        }), {
            method: 'POST',
            headers: new Headers({
                "Authorization": sessionStorage.jwtToken
            })
        }).then(response => response.json())
            .then(data => {
                setFp(data);
                setIsModerationRequired(fProj.status == "NOT_CONSIDERED");
            });
    }

    function startFp() {
        crowdfundInstance.methods.startProject(
            fProj.title,
            fProj.description,
            fProj.days,
            web3.utils.toWei(String(fProj.amountGoal), 'ether'),
        ).send({
            from: accounts[0],
        }).then((res) => {
            const projectInfo = res.events.ProjectStarted.returnValues;
            projectInfo.isLoading = false;
            projectInfo.currentAmount = 0;
            projectInfo.currentState = 0;
            projectInfo.contract = crowdfundProject(projectInfo.contractAddress);
            console.log(projectInfo);
            fetch(`http://localhost:18080/fundraising-projects/start?id=` + fProj.fundraisingProjectId
                + "&contractAddress=" + projectInfo.contractAddress, {
                method: 'POST',
                headers: new Headers({
                    "Authorization": sessionStorage.jwtToken
                })
            }).then(response => response.json())
                .then(data => {
                    setFp(data);
                    setIsInApprovedState(false);
                });
        });
    }

    function invest() {
        const ethAmount = document.getElementById("ethAmount").value;
        const contractAddress = fProj.contractAddress;
        crowdfundProject(contractAddress).methods.contribute().send({
            from: accounts[0],
            value: web3.utils.toWei(String(ethAmount), 'ether'),
        }).then((res) => {
            const currentAmount = parseInt(res.events.FundingReceived.returnValues.currentTotal, 10) / 1000000000;
            fProj.currentAmount = currentAmount;
            fetch(`http://localhost:18080/fundraising-projects/invest?id=` + fProj.fundraisingProjectId
                + "&currentAmount=" + currentAmount, {
                method: 'POST',
                headers: new Headers({
                    "Authorization": sessionStorage.jwtToken
                })
            }).then(response => response.json())
              .then(data => {
                    setFp(data);
                });
        });
    }

    useEffect(() =>{
        setFp(fp)
        setIsInApprovedState(fProj.status == "APPROVED");
        setIsModerationRequired(fProj.status == "NOT_CONSIDERED");
        web3.eth.getAccounts().then((accounts) => {
            setAccounts(accounts);
        });
    },[fp])

    console.log(fProj);

    return (
        <li>
            <div className="bg-light m-5 p-3 fpWrapper">
                <h2 className="p-3">{fProj.title}</h2>
                <div className="p-3">{fProj.description}</div>
                <div className="row p-3">
                    <div className="col-sm">User: {fProj.founder.username}</div>
                    <div className="col-sm">Goal: {fProj.amountGoal}</div>
                    <div className="col-sm">Due date: {fProj.duration.substring(0, fProj.duration.indexOf('T'))}</div>
                    {isModeration > 0 &&
                        <div className="col-sm">Days: {fProj.days}</div>
                    }
                    <div className="col-sm">Raised: {fProj.currentAmount}</div>
                </div>
                <div className="p-3">Tags: </div>
                {isModeration > 0 &&
                    <div>
                        <div className="p-3">Status: {fProj.status}</div>
                        {isModerationRequired > 0 &&
                            <div className="p-1">
                                <button type="button" className="btn btn-primary btn-block m-2"
                                        onClick={() => approve()}>Approve
                                </button>
                                <button type="button" className="btn btn-primary btn-block m-2"
                                        onClick={() => reject()}>Reject
                                </button>
                            </div>
                        }
                    </div>
                }
                {isSelfProfile > 0 && !(isModeration > 0) &&
                    <div>
                        <div className="p-3">Status: {fProj.status}</div>
                    </div>
                }
                {isInApprovedState > 0 && isSelfProfile > 0 &&
                    <button id="startBtn" type="button" className="btn btn-primary btn-block m-2" onClick={() => startFp()}>Start company</button>
                }
                {isModeration <= 0 && isSelfProfile <= 0 &&
                    <div className="input-group m-2 w-25">
                        <input id="ethAmount" type="text" className="form-control" placeholder="ETH amount"
                               aria-label="ETH amount" aria-describedby="basic-addon2"/>
                        <button id="startBtn" type="button" className="btn btn-primary" onClick={() => invest()}>Contribute</button>
                    </div>
                }
            </div>
        </li>
    )
}
