import React, {useState} from "react";
import 'bootstrap/dist/css/bootstrap.css';
import {useParams} from "react-router-dom";
import {useEffect} from "react";

export default function UserInfo() {
    const {userId} = useParams();
    const [investments, setInvestments] = useState([]);

    useEffect(() => {
        loadData();
    },[]);

    function loadData() {
        fetch("http://localhost:18080/user/investments/" + userId, {
            method: 'GET',
            headers: new Headers({
            "Authorization": sessionStorage.jwtToken
            })
        }).then(resp => resp.json())
            .then(data => {
                setInvestments(data);
            })
    }

    return (
        <table className="table">
            <thead>
            <tr>
                <th scope="col">Crowdfunding Company</th>
                <th scope="col"></th>
                <th scope="col">Last</th>
                <th scope="col">Handle</th>
            </tr>
            </thead>
            <tbody>
            {investments.map(investment => {
                return <tr>
                        <th scope="row">{investment.fundraisingProject.title}</th>
                        <td>{investment.fundraisingProject.title}</td>
                        <td>Otto</td>
                        <td>@mdo</td>
                       </tr>
            })}
            </tbody>
        </table>
    )
}
