import React from "react";
import 'bootstrap/dist/css/bootstrap.css';
import './selfProfile.css'
import {useEffect, useState} from "react";

export default function SelfProfile() {
    const [mounted, setMounted] = useState(false);
    const [profile, setProfile] = useState([]);

    if(!mounted) {
        fetch(`http://localhost:18080/user/selfProfile`, {
            method: 'GET',
            headers: new Headers({
                "Authorization": sessionStorage.jwtToken
            })
        }).then(response => response.json())
            .then(data => {
                console.log(data);
                setProfile(data);
            });
    }

    useEffect(() =>{
        setMounted(true)
    },[])

    return (
        <div className="container mb-4 p-3 d-flex justify-content-center">
            <div className="card p-5 profileWrapper">
                <div className="image d-flex flex-column">
                    <div className="d-flex flex-row gap-2">
                        Username: {profile.username}
                    </div>
                    <div className="d-flex flex-row gap-2">
                        Email: {profile.email}
                    </div>
                    <div className="d-flex flex-row gap-2">
                        First name: {profile.firstName}
                    </div>
                    <div className="d-flex flex-row gap-2">
                        Last name: {profile.lastName}
                    </div>
                    <div className="d-flex flex-row gap-2">
                        Patronymic: {profile.patronymic}
                    </div>
                    <div className="d-flex flex-row gap-2">
                        Role: {profile.role}
                    </div>

                    <div className="d-flex flex-row gap-2">
                        Ethereum address: {profile.username}
                    </div>
                    <br/>

                    <div className=" d-flex mt-2">
                        <button className="btn1 btn-dark">My fundraising companies</button>
                    </div>
                </div>
            </div>
        </div>
    );
}
